const gallery=(()=>{"use strict";const e=document.querySelectorAll(".gallery__img"),t=document.querySelector(".img-container"),c=document.querySelector(".img-show"),s=document.querySelector(".img-description"),l=document.querySelector(".nextBtn"),i=document.querySelector(".backBtn"),n=document.querySelector(".exit-icon");let r=0;e.forEach((e,s)=>{e.addEventListener("click",()=>{t.classList.toggle("move"),c.classList.toggle("img-scale"),r=s,o(e.src,e.alt)})}),n.addEventListener("click",()=>{t.classList.toggle("move"),c.classList.toggle("img-scale")});const o=(e,t)=>{c.src=e,s.innerHTML=t,a()};l.addEventListener("click",()=>{r<e.length-1&&(o(e[++r].src,e[r].alt),a())}),i.addEventListener("click",()=>{r>0&&(o(e[--r].src,e[r].alt),a())});const a=()=>{switch(r){case 0:i.classList.add("hideBtn");break;case e.length-1:l.classList.add("hideBtn");break;default:l.classList.remove("hideBtn"),i.classList.remove("hideBtn")}}})();